mapper.select:
SELECT hdr.*,
'enum:status=USER_STATUS' as `status_desc`
FROM iiweb_user hdr
where 1=1

    and hdr.created_date >= #{createdDateFrom}


    and hdr.created_date <= #{createdDateTo}


    and hdr.status >= #{statusFrom}


    and hdr.status <= #{statusTo}


    and hdr.name = #{name}


    and hdr.realname = #{realname}

and hdr.status < 99 order by hdr.id desc
